window.addEventListener('DOMContentLoaded', function() {
  // Track article reading progress
  let maxScroll = 0;
  let readingStartTime = Date.now();

  window.addEventListener('scroll', function() {
    const scrollPercent = Math.round((window.scrollY / (document.body.scrollHeight - window.innerHeight)) * 100);

    if (scrollPercent > maxScroll) {
      maxScroll = scrollPercent;

      // Track reading milestones
      if (maxScroll >= 25 && maxScroll < 50) {
        if (typeof gtag !== 'undefined') {
          gtag('event', 'article_read_25', {
            event_category: 'blog_engagement',
            event_label: 'v1.2_update',
            value: Math.round((Date.now() - readingStartTime) / 1000)
          });
        }
      } else if (maxScroll >= 50 && maxScroll < 75) {
        if (typeof gtag !== 'undefined') {
          gtag('event', 'article_read_50', {
            event_category: 'blog_engagement',
            event_label: 'v1.2_update',
            value: Math.round((Date.now() - readingStartTime) / 1000)
          });
        }
      } else if (maxScroll >= 75) {
        if (typeof gtag !== 'undefined') {
          gtag('event', 'article_read_75', {
            event_category: 'blog_engagement',
            event_label: 'v1.2_update',
            value: Math.round((Date.now() - readingStartTime) / 1000)
          });
        }
        if (typeof fbq !== 'undefined') {
          fbq('track', 'ViewContent', {
            content_name: 'V1.2 Article Completed',
            content_category: 'Blog Engagement'
          });
        }
      }
    }
  });

  // Track CTA clicks with feature context
  const ctaLinks = document.querySelectorAll('.header-cta-btn, .cta-link');
  ctaLinks.forEach(link => {
    link.addEventListener('click', function() {
      if (typeof gtag !== 'undefined') {
        gtag('event', 'blog_cta_click', {
          event_category: 'conversion',
          event_label: 'v1.2_update_to_analyzer',
          value: 1
        });
      }
      if (typeof fbq !== 'undefined') {
        fbq('track', 'Lead', {
          content_name: 'V1.2 Update CTA - Smart Targeting',
          content_category: 'Blog Conversion',
          value: 1.00,
          currency: 'USD'
        });
      }
    });
  });

  // Track feature interest (hover on feature sections)
  const featureSections = document.querySelectorAll('[style*="border-left:4px solid"]');
  featureSections.forEach((section, index) => {
    section.addEventListener('mouseenter', function() {
      const featureNames = ['Smart Focus Selection', 'Industry Intelligence', 'Benchmark Analysis', 'Predictive Scoring'];
      if (typeof gtag !== 'undefined') {
        gtag('event', 'feature_interest', {
          event_category: 'v1.2_features',
          event_label: featureNames[index] || `feature_${index}`,
          value: 1
        });
      }
    });
  });
});
